//给文本框加样式
$(function(){
	$(":text").addClass('ipt');
	$("textarea").addClass('textarea');
	$(":password").addClass('ipt');
	$(":submit").addClass('ipt');
	$(":button").addClass('btn1');
})
$(document).ready(function(){ 
$(':password').focus(function(){ 
$(this).addClass('ipt-on'); 
}); 
})
$(document).ready(function(){ 
$(':text').focus(function(){ 
$(this).addClass('ipt-on'); 
});
$('textarea').focus(function(){ 
$(this).addClass('textarea-on'); 
}); 
$(':text').blur(function(){ 
$(this).removeClass('ipt-on'); 
}); 
$('textarea').blur(function(){ 
$(this).removeClass('textarea-on'); 
}); 
}); 
//展开/收缩更多筛选条件
function showFile() {
 if ($(".more-search").css("display") == "none") {
  $(".more-search").show();
  $("#adv-search").addClass('adv-search-on'); 
 } else {
  $(".more-search").hide();
  $("#adv-search").removeClass('adv-search-on'); 
 }
}
$(document).ready(function(){
$("#adv-search").click(showFile);
})
$(document).ready(function(){
$(".systole").click(showFile);
})
